import React, { useEffect, useState } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import StoreItem from "../../components/StoreItem";
import { addItem, getCategories, getItems } from "../../services/apiServices";
import { Item } from "../../interfaces/ItemType";
import { Modal, Select } from "antd";
import AddItemForm from "../../components/AddItemForm";
import { Category } from "../../interfaces/CategoryType";

const { Option } = Select;

type Props = {};

const Products = (props: Props) => {

  const [products, setProducts] = useState<Array<Item>>([]);
  const [visible, setVisible] = useState(false);
   const [categories, setCategories] = useState<Array<Category>>([]);
  const [shipping, setShipping] = useState("");
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("");
  const [category, setCategory] = useState("");
  // const [photo, setPhoto] = useState<Blob | MediaSource>('');

    useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };

  function handleUpdate() {}

  async function handleSubmit(item: Item) {
    // setVisible(false);
    console.log(item);

  await addItem(item).then((json)=>{
console.log(json);

  })

  }


  return (
    <>
      <Container>
        <Modal onCancel={() => setVisible(false)} footer={null} open={visible}>
         <div className="col-md-9 ">
            <h1>Create Product</h1>
            <div className=" w-75">
              <Select
                bordered={false}
                placeholder="Select a category"
                size="large"
                showSearch
                className="form-select mb-3"
                onChange={(value) => {
                  setCategory(value);
                }}
              >
                {categories?.map((c) => (
                  <Option key={c._id} value={c._id}>
                    {c.name}
                  </Option>
                ))}
              </Select>
              <div className="mb-3">
                <label className="btn btn-outline-secondary col-md-12">
                  {/* {photo ? photo : "Upload Photo"} */}
                  <input
                    type="file"
                    name="photo"
                    accept="image/*"
                    // onChange={(e: any) => setPhoto(e.target.files[0])}
                    hidden
                  />
                </label>
              </div>
              <div className="mb-3">
                {/* {photo && (
                  <div className="text-center">
                    <img
                      src={URL.createObjectURL(photo)}
                      alt="product_photo"
                      height={"200px"}
                      className="img img-responsive"
                    />
                  </div>
                )} */}
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  value={name}
                  placeholder="Product name"
                  className="form-control"
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  value={description}
                  placeholder="Product description"
                  className="form-control"
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  value={price}
                  placeholder="Product price"
                  className="form-control"
                  onChange={(e) => setPrice(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  value={quantity}
                  placeholder="Product quantity"
                  className="form-control"
                  onChange={(e) => setQuantity(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <Select
                  bordered={false}
                  placeholder="Shipping offered?"
                  size="small"
                  showSearch
                  className="form-select mb-3"
                  onChange={(value) => {
                    setShipping(value);
                  }}
                >
                  <Option value="0">No</Option>
                  <Option value="1">Yes</Option>
                </Select>
              </div>
              <div className="mb-3">
                <button className="btn btn-primary" onClick={()=>handleSubmit}>
                  CREATE PRODUCT
                </button>
              </div>
            </div>
          </div>
        </Modal>
        <Container className="d-flex justify-content-end">
          <Button
            variant="outline-success"
            onClick={() => {
              setVisible(true);
            }}
          >
            + Add a Product
          </Button>
        </Container>
        <Row md={2} xs={1} lg={3} className="g-3">
          {products.map((item) => (
            <Col key={item._id}>
              <StoreItem {...item} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
};

export default Products;
