import React, { FormEvent, useContext, useEffect } from "react";
import { Container } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import FormTitle from "../../components/FormTitle";
import { useState } from "react";
import toast from "react-hot-toast";
import { UserContext } from "../../context/userContext";
import { updateUser } from "../../services/apiServices";
import UserMenu from "./UserMenu";
type Props = {};

const Profile = (props: Props) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [address, setAddress] = useState("");
  const [passwordShown, setPasswordShown] = useState(false);
  const [eyeShown, setEyeShown] = useState(false);
  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
    setEyeShown(passwordShown ? false : true);
  };

  const { userData, setUserData } = useContext(UserContext);

  useEffect(() => {
    setFirstName(userData?.user.firstName);
    setLastName(userData?.user.lastName);
    setEmail(userData?.user.email);
    setPhone(userData?.user.phone);
    setAddress(userData?.user.address);
  }, [userData]);

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    updateUser({
      firstName,
      lastName,
      email,
      password,
      phone,
      address,
    }).then((json) => {
      if (!json.success) {
        toast.error(`${json?.error}`);
      } else {
        setPassword("");
        setUserData({ ...userData, user: json.updatedUser });

        let ls: any = localStorage.getItem("user");
        ls = JSON.parse(ls);
        ls.user = json.updatedUser;
        localStorage.setItem("user", JSON.stringify(ls));
        toast.success("Profile updated");
      }
    });
  };

  return (
    <Container className="d-flex">
      <div className="w-25">
        <UserMenu />
      </div>

      <div className="p-4 w-25">
        <div className="d-flex justify-content-center">
          <img
            className="w-50 rounded-circle"
            src={
              "data:image/jpeg;base64,"
            }
            alt=""
          />
        </div>
        <h3 className="text-center">{userData?.user.firstName}</h3>
        <h6 className="text-center text-muted ">{userData?.user.email}</h6>
      </div>
      <div >
        <Form className="w-100 p-4" onSubmit={handleSubmit}>
          <FormTitle mainText="Your Profile" subText="edit below!" />

          <Form.Group className="mb-3" controlId="formBasicFname">
            <Form.Label>
              First name <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              type="text"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicLname">
            <Form.Label>
              Last name <span className="text-muted">(optional)</span>
            </Form.Label>
            <Form.Control
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              type="text"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>
              Email <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              type="email"
              disabled
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>
              Password <span className="text-danger">*</span>
            </Form.Label>
            <div className="d-flex align-items-center justify-content-center">
              <Form.Control
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                type={passwordShown ? "text" : "password"}
              />
              {eyeShown ? (
                <i
                  onClick={togglePasswordVisiblity}
                  className="bi bi-eye-slash-fill ms-1"
                  style={{
                    backgroundColor: "white",
                    fontSize: "1.5rem",
                    cursor: "pointer",
                  }}
                />
              ) : (
                <i
                  onClick={togglePasswordVisiblity}
                  className="bi bi-eye-fill ms-1"
                  style={{
                    backgroundColor: "white",
                    fontSize: "1.5rem",
                    cursor: "pointer",
                  }}
                />
              )}
            </div>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPhone">
            <Form.Label>
              Phone <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              type="text"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicAddress">
            <Form.Label>
              Address <span className="text-danger">*</span>
            </Form.Label>
            <Form.Control
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              type="text"
              required
            />
          </Form.Group>

          <Button variant="outline-primary" type="submit">
            UPDATE
          </Button>
        </Form>
      </div>
    </Container>
  );
};

export default Profile;
