import React, { useEffect, useState } from 'react'
import { Category } from '../../interfaces/CategoryType';
import { Select } from 'antd';
import { getCategories } from '../../services/apiServices';
import AdminMenu from './AdminMenu';
import { Button, Container } from 'react-bootstrap';
const { Option } = Select;

type Props = {}

const EditProduct = (props: Props) => {
     const [categories, setCategories] = useState<Array<Category>>([]);
 const [shipping, setShipping] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [category, setCategory] = useState("");
  const [photo, setPhoto] = useState("");

   useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  function handleClick(){}

  return (
       <div className="col-md-9 d-flex justify-content-center w-100">
             <div className="col-md-3 me-5">
          <AdminMenu />
        </div>
         <Container className="d-flex justify-content-end">
          <Button
            className="mb-5"
            variant="success"
            onClick={() => {
              // setVisible(true);
            }}
          >
            + Add a Product
          </Button>
        </Container>
              <div className=" w-75">
                <h1 className='mb-5 text-center'>Edit a Product</h1>
                <Select
                  bordered={false}
                  placeholder="Select a category"
                  size="small"
                  showSearch
                  className="form-select mb-3"
                  onChange={(value) => {
                    setCategory(value);
                  }}
                >
                  {categories?.map((c) => (
                    <Option key={c._id} value={c._id}>
                      {c.name}
                    </Option>
                  ))}
                </Select>
                <div className="mb-3">
                  <label className=" col-md-12">
                    Add an Image Url <span className="text-danger">*</span>
                    <input
                      type="text"
                      value={photo}
                      placeholder="Product photo"
                      className="form-control"
                      onChange={(e) => setPhoto(e.target.value)}
                    />
                  </label>
                </div>
                <div className="mb-3">
                  {photo && (
                    <div className="text-center">
                      <img
                        src={photo}
                        alt="product_photo"
                        height={"200px"}
                        className="img img-responsive"
                      />
                    </div>
                  )}
                </div>
                <div className="mb-3">
                  <input
                    type="text"
                    value={name}
                    placeholder="Product name"
                    className="form-control"
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="text"
                    value={description}
                    placeholder="Product description"
                    className="form-control"
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="number"
                    value={price}
                    placeholder="Product price"
                    className="form-control"
                    onChange={(e: any) => setPrice(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="number"
                    value={quantity}
                    placeholder="Product quantity"
                    className="form-control"
                    onChange={(e: any) => setQuantity(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <Select
                    bordered={false}
                    placeholder="Shipping offered?"
                    size="small"
                    showSearch
                    className="form-select mb-3"
                    onChange={(value) => {
                      setShipping(value);
                    }}
                  >
                    <Option value={false}>No</Option>
                    <Option value={true}>Yes</Option>
                  </Select>
                </div>
                <div className="mb-3">
                  <button style={{width: '48.5%'}} onClick={handleClick} type="button" className="btn btn-danger  m-1">
                    CANCEL
                  </button>
                  <button style={{width: '48.5%'}} onClick={handleClick} type="button" className="btn btn-primary  m-1">
                    UPDATE
                  </button>
                </div>
              </div>
          </div>
  )
}

export default EditProduct
