import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap'
import FormTitle from '../components/FormTitle'
import { login } from '../services/apiServices'

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleSubmit() {
     login({ email, password })
      .then((json) => {
        if (json && json.status === "fail") {
          console.log(json);
          setLoadCircle(false);
          setPasswordCorrect(false);
          setEmailCorrect(false);
          if (json && json.trys) {
            toast.error(json.tryMessage, { hideProgressBar: true, autoClose: false });
          } else {
            toast.error(json.message, { hideProgressBar: true });
          }

          return;
        }
        if (json.token) {
          setToken(json.token);
          setUser(json);
          setloginMsg(`Welcome back ${json.name} ${json.lastName || ""} ! , logging in...`);
          setUserData(json);
        }
        setLoadCircle(true);
        setTimeout(() => {
          navigate("/");
        }, 2000);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message, { autoClose: false });
        setPasswordCorrect(false);
        setEmailCorrect(false);
        setLoadCircle(false);
      });
  }
  return (
  <Form className="w-50 m-auto">

      <FormTitle mainText="Login below" subText="to start shopping!" />

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email <span className="text-danger">*</span></Form.Label>
        <Form.Control
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password <span className="text-danger">*</span></Form.Label>
        <Form.Control
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
        />
      </Form.Group>

      <Button onClick={handleSubmit} variant="outline-primary" type="button">
        Login
      </Button>
    </Form>
  )
}

export default Login
