import React, { useEffect, useState } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import StoreItem from "../../components/StoreItem";
import { getItems } from "../../services/apiServices";
import { Item } from "../../interfaces/ItemType";
type Props = {};

const Products = (props: Props) => {
  const [products, setProducts] = useState<Array<Item>>([]);

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };

  return (
    <>
<Container>
    <Container className="d-flex justify-content-end"><Button variant="outline-success">+ Add A Product</Button></Container>
      <Row md={2} xs={1} lg={3} className="g-3">
        {products.map((item) => (
          <Col key={item._id}>
            <StoreItem {...item} />
          </Col>
        ))}
      </Row>
      </Container>
    </>
  );
};

export default Products;
