import React, { useEffect, useState } from "react";
import { Button, Card, Col, Container, Nav, Row } from "react-bootstrap";
import {
  addItem,
  deleteItem,
  getCategories,
  getItems,
  getSingleItem,
  updateItem,
} from "../../services/apiServices";
import { Item } from "../../interfaces/ItemType";
import { Modal, Select } from "antd";
import { Category } from "../../interfaces/CategoryType";
import { toast } from "react-toastify";
import { formatCurrency } from "../../utilities/FormatCurrency";
import { NavLink } from "react-router-dom";
import ProductForm from "../../components/ProductForm";

const { Option } = Select;

type Props = {};

const Products = (props: Props) => {
  const [products, setProducts] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [selected, setSelected] = useState<Item | null>(null);
  const [visible, setVisible] = useState(false);
  const [visibleUpdate, setVisibleUpdate] = useState(false);
  const [shipping, setShipping] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [category, setCategory] = useState("");
  const [photo, setPhoto] = useState("");
  const [oneItem, setOneItem] = useState<Item>()

  useEffect(() => {
    getSingle();
  }, []);

  function getSingle() {
    getSingleItem(selected?._id as string).then((json) => {
      setOneItem(json);
    });
  }

  useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };

  async function handleSubmit(item: Item) {
    await addItem(item).then((json) => {
      if (!json.success) {
        toast.error(json.message);
      } else {
        getAllProducts();
        toast.success(`Product created`);
        setVisible(false);
      }
    });
  }

  async function handleUpdate() {
    await updateItem(selected?._id as string, {
      name,
      description,
      price,
      quantity,
      category,
      photo,
      shipping,
    }).then((json) => {
      console.log(json);
    });
  }

  async function handleDelete(pid: string) {
    let answer = window.confirm("Are you sure you want to delete this product");
    if (!answer) return;
    await deleteItem(pid).then((json) => {
      getAllProducts();
      toast.success(json.message);
    });
  }

  return (
    <>
      <Container>
        <Modal onCancel={() => setVisible(false)} footer={null} open={visible}>
          <ProductForm handleSubmit={handleSubmit} />
        </Modal>
        <Container className="d-flex justify-content-between">
          <Nav.Link to={`/dashboard/admin`} as={NavLink} className="ps-3">
            <Button
              className="mb-5"
              variant="outline-primary"
              onClick={() => {
                setVisible(true);
              }}
            >
              Back to Dashboard
            </Button>
          </Nav.Link>
          <Button
            className="mb-5"
            variant="success"
            onClick={() => {
              setVisible(true);
            }}
          >
            + Add a Product
          </Button>
        </Container>
        <Row md={2} xs={1} lg={3} className="g-3">
          {products.map((item) => (
            <Col key={item._id}>
              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={item.photo}
                  height="200px"
                  style={{ objectFit: "cover" }}
                />
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="d-flex justify-content-between align-items-baseline mb-4">
                    <span className="fs-2">{item.name}</span>
                    <span className="fs-2">
                      {item.description?.substring(0, 5)}...
                    </span>
                    <span className="ms-2 text-muted">
                      {formatCurrency(item.price as number)}
                    </span>
                  </Card.Title>
                </Card.Body>
                <Card.Footer className="d-flex justify-content-between">
                  <Button
                    onClick={() => {
                      setSelected(item);
                      setVisibleUpdate(true);
                    }}
                    className=""
                    variant="secondary"
                  >
                    Edit
                  </Button>
                  <Button
                    onClick={() => handleDelete(item._id as string)}
                    className=""
                    variant="outline-danger"
                  >
                    Delete
                  </Button>
                </Card.Footer>
              </Card>
            </Col>
          ))}
          <Modal
            onCancel={() => setVisibleUpdate(false)}
            footer={null}
            open={visibleUpdate}
          >
            <div>
              <div className="col-md-9 ">
                <h1>Update Product</h1>
                <div className=" w-75">
                  <Select
                    bordered={false}
                    placeholder="Select a category"
                    size="small"
                    showSearch
                    className="form-select mb-3"
                    onChange={(value) => {
                      setCategory(value);
                    }}
                  >
                    {categories?.map((c) => (
                      <Option key={c._id} value={c._id}>
                        {c.name}
                      </Option>
                    ))}
                  </Select>
                  <div className="mb-3">
                    <label className=" col-md-12">
                      Add an Image Url <span className="text-danger">*</span>
                      <input
                        type="text"
                        value={photo}
                        placeholder="Product photo"
                        className="form-control"
                        onChange={(e) => setPhoto(e.target.value)}
                      />
                    </label>
                  </div>
                  <div className="mb-3">
                    {photo && (
                      <div className="text-center">
                        <img
                          src={photo}
                          alt="product_photo"
                          height={"200px"}
                          className="img img-responsive"
                        />
                      </div>
                    )}
                  </div>
                  <div className="mb-3">
                    <input
                      type="text"
                      value={selected?.name}
                      placeholder="Product name"
                      className="form-control"
                      onChange={(e) => setName(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <input
                      type="text"
                      value={description}
                      placeholder="Product description"
                      className="form-control"
                      onChange={(e) => setDescription(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <input
                      type="number"
                      value={price}
                      placeholder="Product price"
                      className="form-control"
                      onChange={(e: any) => setPrice(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <input
                      type="number"
                      value={quantity}
                      placeholder="Product quantity"
                      className="form-control"
                      onChange={(e: any) => setQuantity(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <Select
                      bordered={false}
                      placeholder="Shipping offered?"
                      size="small"
                      showSearch
                      className="form-select mb-3"
                      onChange={(value) => {
                        setShipping(value);
                      }}
                    >
                      <Option value={false}>No</Option>
                      <Option value={true}>Yes</Option>
                    </Select>
                  </div>
                  <div className="mb-3">
                    <button
                      onClick={handleUpdate}
                      type="button"
                      className="btn btn-primary"
                    >
                      UPDATE PRODUCT
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </Modal>
        </Row>
      </Container>
    </>
  );
};

export default Products;
