import { Col, Container, Row } from "react-bootstrap";
import StoreItem from "../components/StoreItem";
import { useContext, useEffect, useState } from "react";
import {
  filterItem,
  getCategories,
  getItems,
  getItemsCount,
  getProductList,
} from "../services/apiServices";
import { Item } from "../interfaces/ItemType";
import { UserContext } from "../context/userContext";
import { Checkbox, Radio } from "antd";
import { Category } from "../interfaces/CategoryType";
import { Prices } from "../components/PriceFilters";

const Home = () => {
  const [allItems, setAllItems] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [checked, setChecked] = useState<any>([]);
  const [radio, setRadio] = useState<any>([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const { userData, setUserData } = useContext(UserContext);

  useEffect(() => {
    if (!checked.length || !radio.length) getAllProducts();
  }, [checked.length, radio.length]);

  useEffect(() => {
    getAllCategories();
    getTotalCount();
  }, []);

  const getTotalCount = async () => {
    await getItemsCount().then((json) => {
      console.log(json);
    });
  };

   useEffect(() => {
    if (page === 1) return;
    loadMore();
  }, [page]);

   const loadMore = async () => {
    // try {
    //   setLoading(true);
    //   const { data } = await axios.get(`/api/v1/product/product-list/${page}`);
    //   setLoading(false);
    //   setProducts([...products, ...data.products]);
    // } catch (error) {
    //   setLoading(false);
    //   console.log(error);
    // }
    await getProductList({page}).then((json)=>{
      console.log(json);


    })
  };

  useEffect(() => {
    if (checked.length || radio.length) filterProduct();
  }, [checked, radio]);

  async function getAllCategories() {
    await getCategories().then((json) => {
      setCategories(json);
    });
  }

  async function getAllProducts() {
    await getItems().then((json) => {
      setAllItems(json);
    });
  }

  const handleFilter = (value: any, id: any) => {
    let all = [...checked];
    if (value) {
      all.push(id);
    } else {
      all = all.filter((c) => c !== id);
    }
    setChecked(all);
  };

  const filterProduct = async () => {
    await filterItem({ checked, radio }).then((json) => {
      console.log(json);
      setAllItems(json);

      console.log(allItems);
    });
  };

  return (
    <Container className="d-flex p-0">
      <div className="col-md-2 me-3">
        <h4 className="text-center">Filter By Category</h4>
        <div className="d-flex flex-column">
          {categories.map((c) => (
            <Checkbox
              key={c._id}
              onChange={(e) => handleFilter(e.target.checked, c._id)}
            >
              {c.name}
            </Checkbox>
          ))}
        </div>
        <h4 className="text-center mt-4">Filter By Price</h4>
        <div className="d-flex flex-column">
          <Radio.Group onChange={(e) => setRadio(e.target.value)}>
            {Prices?.map((p) => (
              <div key={p._id}>
                <Radio value={p.array}>{p.name}</Radio>
              </div>
            ))}
          </Radio.Group>
        </div>
        <div className="d-flex flex-column">
          <button
            className="btn btn-outline-danger mt-4"
            onClick={() => window.location.reload()}
          >
            RESET FILTERS
          </button>
        </div>
      </div>
      {allItems?.length < 1 && (
        <div className="w-100 text-center text-danger">No results found</div>
      )}
      <Row md={2} xs={3} lg={3} className="g-3 ms-3 w-100">
        {userData?.firstName}
        {allItems.map((item) => (
          <Col key={item._id}>
            {/* {allItems?.length < 1 && (<div>No results found</div>)} */}
            <StoreItem {...item} />
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Home;
