import React, { useEffect, useState } from 'react'
import { Button, Container, Stack } from 'react-bootstrap'
import { useNavigate } from 'react-router-dom'
import { Item } from '../interfaces/ItemType'
import { getItems } from '../services/apiServices'
import { useShoppingCart } from '../context/ShoppingCartContext'
import CartItem from '../components/CartItem'
import { formatCurrency } from '../utilities/FormatCurrency'

type Props = {}

const CartPage = (props: Props) => {
  const navigate = useNavigate()
  const [products, setProducts] = useState<Array<Item>>([]);
   const { cartItems } = useShoppingCart();

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };


  return (
    <Container className='m-0 mx-0 '>
      <h2>Your cart</h2>
      <Stack gap={3} className='m-0 w-25'>
          {cartItems.map((item) => (
            <CartItem key={item.id} {...item} />
          ))}
          <div className="d-flex justify-content-between flex-column">
            <div className="ms-auto fw-bold fs-5">
              Total{" "}
              {formatCurrency(
                cartItems.reduce((total, cartItem) => {
                  const item = products.find((i) => i._id === cartItem.id);
                  return total + (item?.price || 0) * cartItem.quantity;
                }, 0)
              )}
            </div>
            <div>
              <div className="mt-4">
                <Button variant="outline-primary" className="w-100">
                  CHECKOUT NOW
                </Button>
              </div>
              <div className="my-4">
                <Button
                  className="w-100"
                  variant="warning"
                  onClick={() => {
                    navigate("/")
                  }}
                >
                  CONTINUE SHOPPING
                </Button>
              </div>
            </div>
          </div>
        </Stack>
    </Container>
  )
}

export default CartPage
