import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Category } from "../interfaces/CategoryType";
import { getCategories } from "../services/apiServices";

type Props = {};

const Categories = (props: Props) => {
  const [categories, setCategories] = useState<Array<Category>>([]);

  useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  return (
    <Container>
      <h2 className="text-center mb-5">Shop by Category</h2>
       <Row md={2} xs={3} lg={3} className="g-3 ms-3 w-100">
        {categories.map((c) => (
          <Col xs={3} >
            <Link
              className=""
              to={`/category/${c.slug}`}
            >
              {c.name}
            </Link>

         </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Categories;
