import React, { useEffect, useState } from "react";
import AdminMenu from "./AdminMenu";
import { toast } from "react-toastify";
// import { Modal } from "react-bootstrap";
import { createCategory, getCategories, updateCategory } from "../../services/apiServices";
import { Category } from "../../interfaces/CategoryType";
import { Modal } from "antd";
type Props = {};

const CreateCategory = (props: Props) => {
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [name, setName] = useState("");
  const [visible, setVisible] = useState(false);
  const [selected, setSelected] = useState('');
  const [updatedName, setUpdatedName] = useState("");

  useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    createCategory({
      name,
    }).then((json) => {
      console.log(json);
      if (json.success) {
        toast.success(`${name} was created`);
        getAllCategories();
        setName("");
      }else{
        toast.error(json.message)
      }
    });
  };

  const handleUpdate = async (e: any) => {
    e.preventDefault();
    // try {
    //   const {data} = await axios.put(`/api/v1/category/update-category/${selected._id}`, {name: updatedName})
    //   if(data.success){
    //     toast.success(`${updatedName} was updated`)
    //     setSelected(null)
    //     setUpdatedName("")
    //     setVisible(false)
    //     getAllCategories()
    //   }else{
    //     toast.error(data.message)
    //   }
    // } catch (error) {
    //   console.log(error);
    //   toast.error("Somethng went wrong");
    // }
    updateCategory( selected , {name: updatedName}).then((json)=>{
console.log(json.success);
if(json.success){
  toast.success(`${updatedName} was updated`)
        setSelected('')
        setUpdatedName("")
        setVisible(false)
        getAllCategories()
}else{
        toast.error(json.message)
      }

    })
  };
// handleUpdate()
  const handleDelete = async (pid: any) => {
    try {
      // const {data} = await axios.delete(`/api/v1/category/delete-category/${pid}`)
      // if(data.success){
      //   toast.success(`category deleted`)
      //   getAllCategories()
      // }else{
      //   toast.error(data.message)
      // }
    } catch (error) {
      console.log(error);
      toast.error("Somethng went wrong");
    }
  };

  return (
    <div className="container-fluid m-3 p-3">
      <div className="row">
        <div className="col-md-3">
          <AdminMenu />
        </div>
        <div className="col-md-9">
          <h1>Manage All Categories</h1>
          <div className="p-3 w-50">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control "
                  placeholder="Add new category"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </form>
          </div>
          <div className="w-75">
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">Name</th>
                  <th scope="col">Actions</th>
                </tr>
              </thead>
              <tbody>
                {categories?.map((c: any) => (
                  <tr key={c._id}>
                    <td>{c.name}</td>
                    <td>
                      <button
                        className="btn btn-primary ms-2"
                        onClick={() => {
                          setVisible(true);
                          setUpdatedName(c.name);
                          console.log(updatedName);

                          setSelected(c._id);
                          console.log(selected);

                        }}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-danger ms-2"
                        onClick={() => {
                          handleDelete(c._id);
                        }}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <Modal
            onCancel={() => setVisible(false)}
            footer={null}
            open={visible}
          >
           <form onSubmit={handleUpdate}>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control "
                  placeholder="Add new category"
                  value={updatedName}
                  onChange={(e) => setUpdatedName(e.target.value)}
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </form>
          </Modal>
        </div>
      </div>
    </div>
  );
};

export default CreateCategory;
