import React, { FormEvent, useContext, useEffect } from 'react'
import { Container } from 'react-bootstrap'
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
// import FormTitle from "../components/FormTitle";
import { useState } from "react";
import toast from 'react-hot-toast'
// import { signup } from "../services/apiServices";
import { useNavigate } from "react-router-dom";
import { UserContext } from '../../context/userContext';
import { updateUser } from '../../services/apiServices';
type Props = {}

const Profile = (props: Props) => {
   const navigate = useNavigate();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [address, setAddress] = useState("");
const { userData, setUserData } = useContext(UserContext);
    useEffect(() => {
    setFirstName(userData?.user.firstName);
    setLastName(userData?.user.lastName);
    setEmail(userData?.user.email);
    setPhone(userData?.user.phone);
    // setPassword(userData?.user.password);
    setAddress(userData?.user.address);
  }, [userData]);

    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    // try {
    //   const { data } = await axios.put("/api/v1/auth/profile", {
    //     name,
    //     email,
    //     password,
    //     phone,
    //     address,
    //   });
    //   if (data?.error) {
    //     toast.error(data?.error);
    //   } else {
    //     setAuth({ ...auth, user: data?.updatedUser });
    //     let ls = localStorage.getItem("auth");
    //     ls = JSON.parse(ls);
    //     ls.user = data.updatedUser;
    //     localStorage.setItem("auth", JSON.stringify(ls));
    //     toast.success("Profile updated");
    //   }
    // } catch (error) {
    //   console.log(error);
    //   toast.error("something went wrong");
    // }
    updateUser({
      firstName,
      lastName,
      email,
      password,
      phone,
      address

    }).then((json)=>{
console.log(json);
if(!json.success){
   toast.error(`${json?.message}`);
}else{
  setPassword('')
 setUserData({...userData, user: json.updatedUser})

        let ls: any = localStorage.getItem("user");
        ls = JSON.parse(ls);
        ls.user = json.updatedUser;
        localStorage.setItem("user", JSON.stringify(ls));
        toast.success("Profile updated");
        // navigate(-1)

}

    })
  };

  return (
    <Container className='d-flex'>
      <Container className='p-4'>
<div className='d-flex justify-content-center'>
        <img className='w-50' src={'data:image/jpeg;base64,'} alt="" />
        </div>
        <h3 className='text-center'>name</h3>
        <h6 className='text-center text-muted '>email@gmail.com</h6>
      </Container>
      <Container>
          <Form className="w-100 p-4" onSubmit={handleSubmit}>
      {/* <FormTitle mainText="Sign up below" subText="join now!" /> */}

      <Form.Group className="mb-3" controlId="formBasicFname">
        <Form.Label>
          First name <span className="text-danger">*</span>
        </Form.Label>
        <Form.Control
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          type="text"
          required
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicLname">
        <Form.Label>
          Last name <span className="text-muted">(optional)</span>
        </Form.Label>
        <Form.Control
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          type="text"
          required
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>
          Email <span className="text-danger">*</span>
        </Form.Label>
        <Form.Control
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          disabled
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>
          Password <span className="text-danger">*</span>
        </Form.Label>
        <Form.Control
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPhone">
        <Form.Label>
          Phone <span className="text-danger">*</span>
        </Form.Label>
        <Form.Control
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          type="text"
          required
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicAddress">
        <Form.Label>
          Address <span className="text-danger">*</span>
        </Form.Label>
        <Form.Control
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          type="text"
          required
        />
      </Form.Group>

      <Button  variant="outline-primary" type="submit">
        UPDATE
      </Button>
    </Form>
      </Container>
    </Container>
  )
}

export default Profile
