import { Col, Container, Row } from "react-bootstrap";
import StoreItem from "../components/StoreItem";
import { useContext, useEffect, useState } from "react";
import {
  filterItem,
  getCategories,
  getItemsCount,
  getProductList,
} from "../services/apiServices";
import { Item } from "../interfaces/ItemType";
import { Checkbox, Radio } from "antd";
import { Category } from "../interfaces/CategoryType";
import { Prices } from "../components/PriceFilters";
import { SearchContext } from "../context/SearchContext";
import Skeleton from "../components/Skeleton";
import {Swiper, SwiperSlide} from "swiper/react";
// import { Navigation, Pagination, Scrollbar, A11y, EffectCube } from 'swiper';
import 'swiper/swiper-bundle.min.css';

const slides = [
  {
    title: 'fefef',
    image: 'fefef'
  },
  {
    title: 'fefrerf',
    image: 'fefrerf'
  }
]
const Home = () => {
  const [allItems, setAllItems] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [checked, setChecked] = useState<any>([]);
  const [radio, setRadio] = useState<any>([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const { searchValue } = useContext(SearchContext);
  const [filteredItems, setFilteredItems] = useState<Array<Item>>([]);
  const [skeletonLoading, setSkeletonLoading] = useState(true);


  useEffect(() => {
    const timer = setTimeout(() => {
      setSkeletonLoading(false);
    }, 1200);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (!checked.length || !radio.length) getAllProducts();
  }, [checked.length, radio.length]);

  useEffect(() => {

    getAllCategories();
    getTotalCount();
  }, []);

  const getTotalCount = async () => {
    await getItemsCount().then((json) => {
      setTotal(json.total);
    });
  };

  useEffect(() => {
    if (page === 1) return;
    loadMore();
  }, [page]);

  const loadMore = async () => {
    setLoading(true);
    await getProductList(page).then((json) => {
      setLoading(false);
      setAllItems([...allItems, ...json]);
    });
  };

  useEffect(() => {
    if (checked.length || radio.length) filterProduct();
  }, [checked, radio]);

  async function getAllCategories() {
    await getCategories().then((json) => {
      setCategories(json);
    });
  }

  async function getAllProducts() {
    await getProductList(page).then((json) => {
      setAllItems(json);
    });
  }

  const handleFilter = (value: any, id: any) => {
    let all = [...checked];
    if (value) {
      all.push(id);
    } else {
      all = all.filter((c) => c !== id);
    }
    setChecked(all);
  };

  const filterProduct = async () => {
    await filterItem({ checked, radio }).then((json) => {
      setAllItems(json);
    });
  };

  useEffect(() => {
    const filtered = allItems.filter(
      (item) =>
        item.name?.toLowerCase().includes(searchValue.toLowerCase()) ||
        item.description?.toLowerCase().includes(searchValue.toLowerCase())
    );
    setFilteredItems(filtered);
  }, [searchValue, allItems]);

  return (
    <>
  <Swiper
      // modules={[Navigation, Pagination, Scrollbar, A11y, EffectCube]}
      spaceBetween={50}
      slidesPerView={3}
      navigation
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSlideChange={() => console.log('slide change')}
      onSwiper={(swiper) => console.log(swiper)}
      effect={"cube"}
      cubeEffect={{
        shadow: true,
        slideShadows: true,
        shadowOffset: 20,
        shadowScale: 0.94,
      }}
    >
      {slides.map((slide) => (
        <SwiperSlide key={slide.image}>
          <img src={slide.image} alt={slide.title}/>
        </SwiperSlide>
      ))}
    </Swiper>
      <Container className="d-flex p-0">

        <div className="col-md-2 me-3">
          <h4 className="text-center">Filter By Category</h4>
          <div className="d-flex flex-column">
            {categories.map((c) => (
              <Checkbox
                key={c._id}
                onChange={(e) => handleFilter(e.target.checked, c._id)}
              >
                {c.name}
              </Checkbox>
            ))}
          </div>
          <h4 className="text-center mt-4">Filter By Price</h4>
          <div className="d-flex flex-column">
            <Radio.Group onChange={(e) => setRadio(e.target.value)}>
              {Prices?.map((p) => (
                <div key={p._id}>
                  <Radio value={p.array}>{p.name}</Radio>
                </div>
              ))}
            </Radio.Group>
          </div>
          <div className="d-flex flex-column">
            <button
              className="btn btn-outline-danger mt-4"
              onClick={() => window.location.reload()}
            >
              RESET FILTERS
            </button>
          </div>
        </div>
        {filteredItems?.length < 1 && (
          <div className="w-100 text-center text-danger">No results found</div>
        )}
        <Row md={2} xs={3} lg={3} className="g-3 ms-3 w-100">
          {filteredItems.map((item) => (
            <Col key={item._id}>
              {allItems?.length < 1 && <div>No results found</div>}
              {skeletonLoading ? (
                <Skeleton />
              ) : (
                <StoreItem {...item} slug={item.slug} _id={item._id} />
              )}
            </Col>
          ))}
        </Row>
      </Container>
      <Container className="text-end mt-5">
        {allItems && allItems.length < total && (
          <button
            className="btn btn-warning"
            onClick={(e) => {
              e.preventDefault();
              setPage(page + 1);
            }}
          >
            {loading ? "Loading..." : "Loadmore"}
          </button>
        )}
      </Container>
    </>
  );
};

export default Home;
