import React, { useEffect, useState } from "react";
import { Button, Card, Col, Container, Nav, Row } from "react-bootstrap";
import {
  addItem,
  deleteItem,
  getCategories,
  getItems,
  getSingleItem,
  updateItem,
} from "../../services/apiServices";
import { Item } from "../../interfaces/ItemType";
import { Modal, Select } from "antd";
import { Category } from "../../interfaces/CategoryType";
import { toast } from "react-toastify";
import { formatCurrency } from "../../utilities/FormatCurrency";
import { Link, NavLink } from "react-router-dom";
import ProductForm from "../../components/ProductForm";
import AdminMenu from "./AdminMenu";

const { Option } = Select;

type Props = {};

const Products = (props: Props) => {
  const [products, setProducts] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [selected, setSelected] = useState('');
  const [visible, setVisible] = useState(false);
  const [visibleUpdate, setVisibleUpdate] = useState(false);
  const [shipping, setShipping] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [category, setCategory] = useState("");
  const [photo, setPhoto] = useState("");
  const [oneItem, setOneItem] = useState<Array<Item>>([])

  useEffect(() => {
    getSingle();
  }, []);

  function getSingle() {

    getSingleItem(selected).then((json) => {

      console.log(json);
      console.log(selected);


      // setOneItem(json);
    });
  }

  useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };

  async function handleSubmit(item: Item) {
    await addItem(item).then((json) => {
      if (!json.success) {
        toast.error(json.message);
      } else {
        getAllProducts();
        toast.success(`Product created`);
        setVisible(false);
      }
    });
  }

  async function handleUpdate() {
    await updateItem(selected?._id as string, {
      name,
      description,
      price,
      quantity,
      category,
      photo,
      shipping,
    }).then((json) => {
      console.log(json);
    });
  }

  async function handleDelete(pid: string) {
    let answer = window.confirm("Are you sure you want to delete this product");
    if (!answer) return;
    await deleteItem(pid).then((json) => {
      getAllProducts();
      toast.success(json.message);
    });
  }

  return (
    <Container className="d-flex">

          <div className="col-md-3">
          <AdminMenu />
        </div>
        <Modal onCancel={() => setVisible(false)} footer={null} open={visible}>
          <ProductForm handleSubmit={handleSubmit} />
        </Modal>
        <Container>
        <Container className="d-flex justify-content-end">
          <Button
            className="mb-5"
            variant="success"
            onClick={() => {
              setVisible(true);
            }}
          >
            + Add a Product
          </Button>
        </Container>
        <Row md={2} xs={1} lg={3} className="g-3">
          {products.map((item) => (
            <Col key={item._id}>
              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={item.photo}
                  height="200px"
                  style={{ objectFit: "cover" }}
                />
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="d-flex justify-content-between align-items-baseline mb-4">
                    <span className="fs-4">{item.name}</span><br/>
                    {/* <span className="fs-6">
                      {item.description?.substring(0, 5)}...
                    </span> */}
                    <span className="ms-2 text-muted">
                      {formatCurrency(item.price as number)}
                    </span>
                  </Card.Title>
                  <Card.Title className="d-flex justify-content-between align-items-baseline mb-4">
                    {/* <span className="fs-4">{item.name}</span><br/> */}
                    <span className="fs-5">
                      {item.description?.substring(0, 30)}...
                    </span>
                    {/* <span className="ms-2 text-muted">
                      {formatCurrency(item.price as number)}
                    </span> */}
                  </Card.Title>
                  <Card.Title>
                    <span className="fs-6">Shipping: {JSON.stringify(item.shipping)? "Yes": "No"}</span>
                  </Card.Title>
                  <Card.Title>
                    <span className="fs-6">Category: {JSON.stringify(item.category.name)}</span>
                  </Card.Title>
                </Card.Body>
                <Card.Footer className="d-flex justify-content-between">
                  <Link to={`/dashboard/admin/products/${item._id}`}>
                  <Button
                    onClick={() => {
                      setSelected(item.slug);
                      setVisibleUpdate(true);
                       getSingle();
                    }}
                    className=""
                    variant="secondary"
                  >
                    Edit
                  </Button>
                  </Link>
                  <Button
                    onClick={() => handleDelete(item._id as string)}
                    className=""
                    variant="outline-danger"
                  >
                    Delete
                  </Button>
                </Card.Footer>
              </Card>
            </Col>
          ))}

        </Row>
</Container>
    </Container>
  );
};

export default Products;
