import { Button, Card } from "react-bootstrap";
import { formatCurrency } from "../utilities/FormatCurrency";
import { useShoppingCart } from "../context/ShoppingCartContext";

type StoreItemProp = {
  id?: number;
  name?: string;
  price?: number;
  imgUrl?: string;
  itemId?: number
};

const StoreItem = ({id, itemId, name, price, imgUrl }: StoreItemProp) => {
  const { getItemQuantity, increaseCartQuantity, decreaseCartQuantity, removeFromCart} = useShoppingCart()
  const quantity = getItemQuantity(itemId as number)
  console.log(quantity);

  return (
    <Card className="h-100">
      <Card.Img
        variant="top"
        src={imgUrl}
        height="200px"
        style={{ objectFit: "cover" }}
      />
      <Card.Body className="d-flex flex-column">
    <Card.Title className="d-flex justify-content-between align-items-baseline mb-4">
      <span className="fs-2">{name}</span>
      <span className="ms-2 text-muted">{formatCurrency(price as number)}</span>
    </Card.Title>
    <div className="mt-auto">
      {quantity < 1 ? (
        <Button className="w-100" onClick={()=>increaseCartQuantity(itemId as number)}>+ Add to cart</Button>
      ): (
      <div className="d-flex flex-column align-items-center " style={{gap:'.5rem'}}>
        <div className="d-flex align-items-center justify-content-center" style={{gap:'.5rem'}}>
          <Button onClick={()=>decreaseCartQuantity(id as number)}>-</Button>
          <div>
          <span className="fs-3 me-1">{quantity}</span> 
          in cart
          </div>
          <Button onClick={()=>increaseCartQuantity(id as number)}>+</Button>
        </div>
        <Button variant="danger" size="sm" onClick={()=>removeFromCart(id as number)}>Remove</Button>
      </div> 
      )}
    </div>
      </Card.Body>
    </Card>
  );
};

export default StoreItem;
