import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import FormTitle from "../components/FormTitle";
import { useState } from "react";
import { toast } from "react-toastify";
import { signup } from "../services/apiServices";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const navigate = useNavigate();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");

  function validate(): boolean {
    if (!firstName || firstName.length < 2) {
      toast.error("Name is required.");
      return false;
    }
    // eslint-disable-next-line no-useless-escape
    const emailRe =/[a-z0-9\._%+!$&*=^|~#%'`?{}/\-]+@([a-z0-9\-]+\.){1,}([a-z]{2,16})/;
    if (!emailRe.test(email)) {
      toast.error("A valid email address is required.");
      return false;
    }

    if (!phone || phone.length < 10 || phone.length > 250) {
      toast.error("Must submit a valid phone number.");
      return false;
    }

    if (!password || password.length < 8) {
      toast.error("Password must contain at least 8 characters.");
      return false;
    }
    return true;
  }

  function handleSubmit() {
    if (!validate()) return;

    signup({
      firstName,
      lastName,
      email,
      phone,
      password,
    }).then((user) => {
      if (user.error) {
        toast.error(user.error);
      } else {
        navigate("/login");
      }
    });
  }

  return (
    <Form className="w-50 m-auto">
      <FormTitle mainText="Sign up below" subText="join now!" />

      <Form.Group className="mb-3" controlId="formBasicFname">
        <Form.Label>First name *</Form.Label>
        <Form.Control
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          type="text"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicLname">
        <Form.Label>Last name (optional)</Form.Label>
        <Form.Control
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          type="text"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email *</Form.Label>
        <Form.Control
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPhone">
        <Form.Label>Phone *</Form.Label>
        <Form.Control
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          type="text"
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password *</Form.Label>
        <Form.Control
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
        />
      </Form.Group>

      <Button onClick={handleSubmit} variant="outline-primary" type="button">
        Submit
      </Button>
    </Form>
  );
};

export default Signup;
