import React, { useEffect, useState } from 'react'
import { Container } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { getItemCategory } from '../services/apiServices';
import { Item } from '../interfaces/ItemType';
// import { getSingleCategory } from '../services/apiServices';

type Props = {}

const CategoryProducts = (props: Props) => {
  const params = useParams();
  const navigate = useNavigate();
  const [products, setProducts] = useState<Array<Item>>([]);
  const [category, setCategory] = useState([]);

  useEffect(() => {
    if (params.slug) getProductByCat();

  }, [params.slug]);

  const getProductByCat = async () => {
    // try {
    //   const { data } = await axios.get(
    //     `/api/v1/product/product-category/${params.slug}`
    //   );
    //   setProducts(data?.products);
    //   setCategory(data?.category);
    // } catch (error) {
    //   console.log(error);
    // }
    // await getSingleCategory(params?.slug).then(()=>{

    // })
    await getItemCategory(params.slug as string).then((json)=>{
setProducts(json)

    })
  };

  return (
   <Container>
     {/* <h2 className="text-center"> Category - {category?.name}</h2> */}
        <h5 className="text-center">{products.length} results found</h5>
        <div className="row">
           <div className="d-flex flex-wrap">
            {products?.map((p) => (
              <div key={p._id} className="card m-2 h-100" style={{ width: "18rem" }}>
                <img
                  src={`/api/v1/product/product-photo/${p._id}`}
                  className="card-img-top"
                  alt={p.name}
                  style={{ objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title">{p.name}</h5>
                  <p className="card-text">
                    {p.description.substring(0, 30)}...
                  </p>
                  <p className="card-text">${p.price}</p>
                  <button className="btn btn-primary ms-1" onClick={()=>navigate(`/product/${p.slug}`)}>See details</button>
                  <button className="btn btn-secondary ms-1">
                    Add to cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
   </Container>
  )
}

export default CategoryProducts
