import { Col, Container, Row } from "react-bootstrap";
import StoreItem from "../components/StoreItem";
import { useContext, useEffect, useState } from "react";
import { getCategories, getItems } from "../services/apiServices";
import { Item } from "../interfaces/ItemType";
import { UserContext } from "../context/userContext";
import { Checkbox, Radio } from "antd";
import { Category } from "../interfaces/CategoryType";
import { Prices } from "../components/PriceFilters";

const Home = () => {
  const [allItems, setAllItems] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [checked, setChecked] = useState<Array<string>>([]);
  const [radio, setRadio] = useState([]);
  const { userData, setUserData } = useContext(UserContext);

  useEffect(() => {
    if (!checked.length || !radio.length) getItems().then((json) => {
      setAllItems(json);
    });
  }, [checked.length, radio.length]);

  useEffect(() => {
    getAllCategories();
    // getTotalCount();
  }, []);

  async function getAllCategories() {
    await getCategories().then((json) => {
      setCategories(json);
    });
  }

    const handleFilter = (value:any, id:any) => {
    let all = [...checked];
    if (value) {
      all.push(id);
    } else {
      all = all.filter((c) => c !== id);
    }
    setChecked(all);
  };

  return (
    <Container className="d-flex p-0">
      <div className="col-md-2 me-3">
        <h4 className="text-center">Filter By Category</h4>
        <div className="d-flex flex-column">
          {categories.map((c) => (

            <Checkbox
              key={c._id}
              onChange={(e) => handleFilter(e.target.checked, c._id)}
            >
              {c.name}
            </Checkbox>
          ))}
        </div>
        <h4 className="text-center mt-4">Filter By Price</h4>
        <div className="d-flex flex-column">
          <Radio.Group
          // onChange={(e) => setRadio(e.target.value)}
          >
            {Prices?.map((p) => (
            <div key={p._id}>
              <Radio value={p.array}>{p.name}</Radio>
            </div>
             ))}
          </Radio.Group>
        </div>
        <div className="d-flex flex-column">
          <button
            className="btn btn-outline-danger mt-4"
            onClick={() => window.location.reload()}
          >
            RESETFILTERS
          </button>
        </div>
      </div>

      <Row md={2} xs={1} lg={3} className="g-3 ms-3">
        {userData?.firstName}
        {allItems.map((item) => (
          <Col key={item._id}>
            <StoreItem {...item} />
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Home;
