import React, { useEffect, useState } from "react";
import { Button, Card, Col, Container, Nav, Row } from "react-bootstrap";
import StoreItem from "../../components/StoreItem";
import { addItem, deleteItem, getCategories, getItems } from "../../services/apiServices";
import { Item } from "../../interfaces/ItemType";
import { Modal, Select } from "antd";
import { Category } from "../../interfaces/CategoryType";
import { toast } from "react-toastify";
import { formatCurrency } from "../../utilities/FormatCurrency";
import { NavLink } from "react-router-dom";

const { Option } = Select;

type Props = {};

const Products = (props: Props) => {
  const [products, setProducts] = useState<Array<Item>>([]);
  const [categories, setCategories] = useState<Array<Category>>([]);
  const [shipping, setShipping] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [category, setCategory] = useState("");
  const [photo, setPhoto] = useState("");
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    getAllCategories();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  useEffect(() => {
    getAllProducts();
  }, []);

  const getAllProducts = async () => {
    await getItems().then((json) => {
      setProducts(json);
    });
  };

  async function handleSubmit(e: any) {
    e.preventDefault();

    console.log(category);

    await addItem({
      name,
      quantity,
      shipping,
      description,
      price,
      category,
      photo,
    }).then((json) => {
      if (!json.success) {
       toast.error(json.message)
      }else{
 getAllProducts();
        toast.success(`${name} was created`);
        setVisible(false);
      }
    });
  }

  async function handleDelete(pid: string){

    let answer = window.confirm('Are you sure you want to delete this product')
 if(!answer)return
 await deleteItem(pid).then((json)=>{
  console.log(json);

 })
  }

  return (
    <>
      <Container>
        <Modal onCancel={() => setVisible(false)} footer={null} open={visible}>
          <form encType="multipart/form-data" onSubmit={handleSubmit}>
            <div className="col-md-9 ">
              <h1>Create a Product</h1>
              <div className=" w-75">
                <Select
                  bordered={false}
                  placeholder="Select a category"
                  size="small"
                  showSearch
                  className="form-select mb-3"
                  onChange={(value) => {
                    setCategory(value);
                  }}
                >
                  {categories?.map((c) => (
                    <Option key={c._id} value={c._id}>
                      {c.name}
                    </Option>
                  ))}
                </Select>
                <div className="mb-3">
                  <label className=" col-md-12">
                    Add an Image Url <span className="text-danger">*</span>
                    <input
                      type="text"
                      value={photo}
                      placeholder="Product photo"
                      className="form-control"
                      onChange={(e) => setPhoto(e.target.value)}
                    />
                  </label>
                </div>
                <div className="mb-3">
                  {photo && (
                    <div className="text-center">
                      <img
                        src={photo}
                        alt="product_photo"
                        height={"200px"}
                        className="img img-responsive"
                      />
                    </div>
                  )}
                </div>
                <div className="mb-3">
                  <input
                    type="text"
                    value={name}
                    placeholder="Product name"
                    className="form-control"
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="text"
                    value={description}
                    placeholder="Product description"
                    className="form-control"
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="number"
                    value={price}
                    placeholder="Product price"
                    className="form-control"
                    onChange={(e: any) => setPrice(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="number"
                    value={quantity}
                    placeholder="Product quantity"
                    className="form-control"
                    onChange={(e: any) => setQuantity(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <Select
                    bordered={false}
                    placeholder="Shipping offered?"
                    size="small"
                    showSearch
                    className="form-select mb-3"
                    onChange={(value) => {
                      setShipping(value);
                    }}
                  >
                    <Option value={false}>No</Option>
                    <Option value={true}>Yes</Option>
                  </Select>
                </div>
                <div className="mb-3">
                  <button type="submit" className="btn btn-primary">
                    CREATE PRODUCT
                  </button>
                </div>
              </div>
            </div>
          </form>
        </Modal>
        <Container className="d-flex justify-content-between">
           <Nav.Link to={`/dashboard/admin`} as={NavLink} className="ps-3">

          <Button
            className="mb-5"
            variant="outline-primary"
            onClick={() => {
              setVisible(true);
            }}
          >
             Back to Dashboard
          </Button>
           </Nav.Link>
          <Button
            className="mb-5"
            variant="success"
            onClick={() => {
              setVisible(true);
            }}
          >
            + Add a Product
          </Button>
        </Container>
        <Row md={2} xs={1} lg={3} className="g-3">
          {products.map((item) => (
            <Col key={item._id}>

              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={item.photo}
                  height="200px"
                  style={{ objectFit: "cover" }}
                />
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="d-flex justify-content-between align-items-baseline mb-4">
                    <span className="fs-2">{item.name}</span>
                    <span className="fs-2">{item.description?.substring(0, 5)}...</span>
                    <span className="ms-2 text-muted">
                      {formatCurrency(item.price as number)}
                    </span>
                  </Card.Title>
                </Card.Body>
                <Card.Footer className="d-flex justify-content-between">
                <Button onClick={()=>handleDelete(item._id)} className="" variant="secondary">Edit</Button>
                <Button className="" variant="outline-danger">Delete</Button>
                </Card.Footer>
              </Card>

            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
};

export default Products;
