import { Col, Container, Row } from "react-bootstrap";
import StoreItem from "../components/StoreItem";
import { useContext, useEffect, useState } from "react";
import { getCategories, getItems } from "../services/apiServices";
import { Item } from "../interfaces/ItemType";
import { UserContext } from "../context/userContext";
import { Checkbox, Radio } from "antd";
import { Category } from "../interfaces/CategoryType";

const Home = () => {
  const [allItems, setAllItems] = useState<Array<Item>>([]);
    const [categories, setCategories] = useState<Array<Category>>([]);
  const { userData, setUserData } = useContext(UserContext);
  useEffect(() => {
    getItems().then((json) => {
      setAllItems(json);
    });
  }, []);

   useEffect(() => {
    getAllCategories();
    // getTotalCount();
  }, []);

  function getAllCategories() {
    getCategories().then((json) => {
      setCategories(json);
    });
  }

  function handleFilter(){

  }

  return (

    <Container className="d-flex p-0">
       <div className="col-md-2">
          <h4 className="text-center">Filter By Category</h4>
          <div className="d-flex flex-column">
            {categories?.map((c) => (
              <Checkbox
                key={c._id}
                onChange={(e) => handleFilter(e.target.checked, c._id)}
              >
                {c.name}
              </Checkbox>
             ))} 
          </div>
          <h4 className="text-center mt-4">Filter By Price</h4>
          <div className="d-flex flex-column">
            <Radio.Group 
            // onChange={(e) => setRadio(e.target.value)}
            >
              {/* {Prices?.map((p) => ( */}
                <div >
                  <Radio value={'test'}>test</Radio>
                </div>
              {/* ))} */}
            </Radio.Group>
          </div>
          <div className="d-flex flex-column">
            <button
              className="btn btn-danger"
              onClick={() => window.location.reload()}
            >
              RESETFILTERS
            </button>
          </div>
        </div>

      <Row md={2} xs={1} lg={3} className="g-3 ms-3">
        {userData?.firstName}
        {allItems.map((item) => (
          <Col key={item._id}>
            <StoreItem {...item} />
          </Col>
        ))}
      </Row>
       </Container>

  );
};

export default Home;
