import { Button, Offcanvas, Stack } from "react-bootstrap"
import { useShoppingCart } from "../context/ShoppingCartContext"
// import storeItems from "../data/items.json"
import { formatCurrency } from "../utilities/FormatCurrency"
import CartItem from "./CartItem"
import { useEffect, useState } from "react"
import { Item } from "../interfaces/ItemType"
import { getItems } from "../services/apiServices"

type ShoppingCartProps = {
  isOpen: boolean
}

const ShoppingCart = ({ isOpen }: ShoppingCartProps) => {
  const { closeCart, cartItems } = useShoppingCart()
  //  const [allItems, setAllItems] = useState<Array<Item>>([])

    const [products, setProducts] = useState<Array<Item>>([]);

  useEffect(()=>{
    getAllProducts()
  }, [])

  const getAllProducts = async () => {
await getItems().then((json)=>{

      setProducts(json)
    })
  };

  return (
     <Offcanvas show={isOpen} onHide={closeCart} placement="end">
      <Offcanvas.Header closeButton>
        <Offcanvas.Title>Cart</Offcanvas.Title>
      </Offcanvas.Header>
      <Offcanvas.Body>
        <Stack gap={3}>
          {cartItems.map(item => (
            <CartItem key={item.id} {...item} />
          ))}
          <div style={{height: '500px'}} className="d-flex justify-content-between">
          <div className="ms-auto fw-bold fs-5">
            Total{" "}
            {formatCurrency(
              cartItems.reduce((total, cartItem) => {
                const item = products.find(i => i._id === cartItem.id)
                return total + (item?.price || 0) * cartItem.quantity
              }, 0)
            )}
          </div>
          <div>
          <div>
            <Button variant="outline-primary" className="w-100">CHECKOUT NOW</Button>

          </div>
          <div>
             <Button className="w-100" variant="warning">GO TO CART</Button>
          </div>
          </div>
          </div>
        </Stack>
      </Offcanvas.Body>
    </Offcanvas>
  )
}

export default ShoppingCart
